import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import API_BASE from "../api";

export default function Users() {
  const [users, setUsers] = useState([]);

  // preia utilizatorii
  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const res = await axios.get(`${API_BASE}/users`);
        setUsers(res.data);
      } catch (err) {
        console.error("Eroare la preluarea utilizatorilor:", err);
        alert("Nu s-au putut încărca utilizatorii. Verifică serverul.");
      }
    };
    fetchUsers();
  }, []);

  // șterge utilizator
  const deleteUser = async (id) => {
    if (!window.confirm("Esti sigur că vrei să ștergi utilizatorul?")) return;

    try {
      await axios.delete(`${API_BASE}/users/${id}`);
      setUsers(users.filter((u) => u.id !== id));
    } catch (err) {
      console.error("Eroare la ștergerea utilizatorului:", err);
      alert("Nu s-a putut șterge utilizatorul. Verifică serverul.");
    }
  };

  return (
    <div>
      <h1>Utilizatori</h1>

      <Link to="/add-user">
        <button className="button">Adaugă Utilizator</button>
      </Link>

      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Nume</th>
            <th>Email</th>
            <th>Telefon</th>
            <th>Adresă</th>
            <th>Acțiuni</th>
          </tr>
        </thead>

        <tbody>
          {users.map((u) => (
            <tr key={u.id}>
              <td>{u.id}</td>
              <td>{u.name}</td>
              <td>{u.email}</td>
              <td>{u.phone}</td>
              <td>{u.address}</td>
              <td>
                <Link to={`/edit-user/${u.id}`}>
                  <button className="button">Editează</button>
                </Link>
                <button
                  className="delete-btn"
                  onClick={() => deleteUser(u.id)}
                >
                  Șterge
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
