import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

export default function Users() {
  const [users, setUsers] = useState([]);

  // preia utilizatorii
  useEffect(() => {
    axios.get("http://127.0.0.1:5000/users")
      .then(res => setUsers(res.data))
      .catch(err => console.error(err));
  }, []);

  // șterge utilizator
  const deleteUser = (id) => {
    if (!window.confirm("Esti sigur că vrei să ștergi utilizatorul?")) return;

    axios.delete(`http://127.0.0.1:5000/users/${id}`)
      .then(() => setUsers(users.filter(u => u.id !== id)));
  };

  return (
    <div>
      <h1>Utilizatori</h1>

      <Link to="/add-user">
        <button className="button">Adaugă Utilizator</button>
      </Link>

      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Nume</th>
            <th>Email</th>
            <th>Acțiuni</th>
          </tr>
        </thead>

        <tbody>
          {users.map(u => (
            <tr key={u.id}>
              <td>{u.id}</td>
              <td>{u.name}</td>
              <td>{u.email}</td>
              <td>
                <button className="delete-btn" onClick={() => deleteUser(u.id)}>
                  Șterge
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
