import { useState } from "react";
import axios from "axios";
import "../styles/library.css";
import { useNavigate } from "react-router-dom";
import API_BASE from "../api";   // ðŸ”¥ importÄƒm API_BASE

export default function AddBook() {
  const [title, setTitle] = useState("");
  const [author, setAuthor] = useState("");
  const [year, setYear] = useState("");

  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      await axios.post(`${API_BASE}/books`, {   // ðŸ”¥ folosim API_BASE
        title,
        author,
        year,
      });
      navigate("/books");
    } catch (error) {
      console.error("Eroare la adÄƒugarea cÄƒrÈ›ii:", error);
      alert("Nu s-a putut adÄƒuga cartea. VerificÄƒ dacÄƒ serverul este pornit.");
    }
  };

  return (
    <div>
      <h1>AdaugÄƒ o carte nouÄƒ</h1>

      <form onSubmit={handleSubmit}>
        <label>Titlu:</label>
        <input
          type="text"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />

        <label>Autor:</label>
        <input
          type="text"
          value={author}
          onChange={(e) => setAuthor(e.target.value)}
          required
        />

        <label>An:</label>
        <input
          type="text"
          value={year}
          onChange={(e) => setYear(e.target.value)}
          required
        />

        <button type="submit" className="button">
          AdaugÄƒ carte
        </button>
      </form>
    </div>
  );
}
