import { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

export default function AddLoan() {
  const [users, setUsers] = useState([]);
  const [books, setBooks] = useState([]);

  const [userId, setUserId] = useState("");
  const [bookId, setBookId] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    axios.get("http://localhost:5000/users").then((r) => setUsers(r.data));
    axios.get("http://localhost:5000/books").then((r) => setBooks(r.data));
  }, []);

  const addLoan = async (e) => {
    e.preventDefault();

    await axios.post("http://localhost:5000/loans", {
      user_id: userId,
      book_id: bookId
    });

    navigate("/loans");
  };

  return (
    <div>
      <h1>Adaugă împrumut</h1>

      <form onSubmit={addLoan}>
        <label>Utilizator:</label>
        <select value={userId} onChange={(e) => setUserId(e.target.value)}>
          <option value="">Alege utilizatorul</option>
          {users.map((u) => (
            <option key={u.id} value={u.id}>{u.name}</option>
          ))}
        </select>

        <label>Carte:</label>
        <select value={bookId} onChange={(e) => setBookId(e.target.value)}>
          <option value="">Alege cartea</option>
          {books.map((b) => (
            <option key={b.id} value={b.id}>{b.title}</option>
          ))}
        </select>

        <button className="button" type="submit">
          Creează împrumut
        </button>
      </form>
    </div>
  );
}
