import { useEffect, useState } from "react";
import axios from "axios";
import "../styles/library.css";
import { useNavigate } from "react-router-dom";

export default function Loans() {
  const [loans, setLoans] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    axios
      .get("http://localhost:5000/loans")
      .then((res) => setLoans(res.data))
      .catch((err) => console.error("Eroare la preluarea împrumuturilor:", err));
  }, []);

  const returnLoan = async (id) => {
    await axios.put(`http://localhost:5000/loans/${id}/return`);
    setLoans(
      loans.map((loan) =>
        loan.id === id ? { ...loan, returned: true } : loan
      )
    );
  };

  return (
    <div>
      <h1>Împrumuturi</h1>

      <div style={{ textAlign: "center", marginBottom: 25 }}>
        <button className="button" onClick={() => navigate("/add-loan")}>
          Adaugă împrumut
        </button>
      </div>

      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Utilizator</th>
            <th>Carte</th>
            <th>Dată împrumut</th>
            <th>Returnat</th>
            <th>Acțiuni</th>
          </tr>
        </thead>
        <tbody>
          {loans.map((loan) => (
            <tr key={loan.id}>
              <td>{loan.id}</td>
              <td>{loan.user_name}</td>
              <td>{loan.book_title}</td>
              <td>{loan.loan_date}</td>
              <td>{loan.returned ? "Da" : "Nu"}</td>
              <td>
                {!loan.returned && (
                  <button
                    className="button"
                    onClick={() => returnLoan(loan.id)}
                  >
                    Returnează
                  </button>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
